import React, { Component, useEffect, useState } from "react";
import RankingSidebar from "../common/RankingSidebar";
import MangaService from "../../services/MangaService";
import HeaderComponent from "../common/Header";
import NavigationComponent from "../common/Navigation";
import Footer from "../common/Footer";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

export function MangaPage(props) {
  return (
    <div>
      <HeaderComponent />
      <NavigationComponent />
      <main class="my-4">
        <div class="container">
          <div class="row">
            <div class="col-9">
              <MainContent isLoggedIn={props.isLoggedIn} />
            </div>
            <div class="col-3">
              <RankingSidebar />
            </div>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}




function ChapterTable(props) {
  return (
    <table className="chapters-table" style={{ width: "100%" }}>
      <tr>
        <th class="text-start">Tên chương</th>
        <th>Cập nhật</th>
        <th>Lượt xem</th>
      </tr>
      {props.chapters.map((chapter) => (
        <tr>
          <td class="text-start">
            <Link to={`chapters/${chapter.id}`}>{chapter.title}</Link>
          </td>
          <td>
            <UploadedDateTimeByChapterId chapterid={chapter.id} />
          </td>
          <td>{chapter.views}</td>
        </tr>
      ))}
    </table>
  );
}

function UploadedDateTimeByChapterId(props) {
  const [datetime, setDatetime] = useState([]);

  useEffect(() => {
    MangaService.getUploadedDateTimeByChapterId(props.chapterid)
      .then((res) => {
        setDatetime(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  });

  return <>{datetime}</>;
}

function GenreItem(props) {
  const [isHover, setIsHover] = useState(false);

  const style = {
    color: isHover ? "white" : "black",
    border: "1px solid var(--orange-dark)",
    backgroundColor: isHover ? "var(--orange-dark)" : "",
  };

  return (
    <Link
      to={`mangas/${props.genre}`}
      class="rounded-pill px-2 py-1 me-2"
      style={style}
      onMouseEnter={() => setIsHover(true)}
      onMouseLeave={() => setIsHover(false)}
    >
      {props.genre}
    </Link>
  );
}

function mapStateToProps(state) {
  const { isLoggedIn, user } = state.auth;
  return { isLoggedIn, user };
}

export default connect(mapStateToProps)(MangaPage);
