import React, { Component, useState } from "react";
import Footer from "../../common/Footer";
import Header from "../../common/Header";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import {authenticateUser} from "../auth/authActions"

export default function LoginPage(props) {
  const [error, setError] = useState();
  const [show, setShow] = useState(true);

  const initialState = {
    email: "",
    password: "",
  };

  const [user, setUser] = useState(initialState);

  const credentialChange = (event) => {
    const { name, value } = event.target;
    setUser({ ...user, [name]: value });
  };

  const dispatch = useDispatch();

  const validateUser = () => {
    dispatch(authenticateUser(user.email, user.password))
      .then((response) => {
        console.log(response.data);
        return props.history.push("/home");
      })
      .catch((error) => {
        console.log(error.message);
        setShow(true);
        resetLoginForm();
        setError("Invalid email and password");
      });
  };

  return (
    <div>
      <Header></Header>
      <div class="container my-5">
        <div class="row">
          <div class="col-3"></div>
          <div class="col-6">
            <WelcomeTagline />
            <div className="not-a-memeber mb-2 d-flex justify-content-end align-items-center">
              <span>Không phải thành viên? </span>
              <span class="btn btn-info ms-2">
                <Link to="/signup">Đăng ký</Link>
              </span>
            </div>
            <div className="login-box border">
              <div
                className="login-form my-5"
                style={{ marginLeft: "100px", marginRight: "100px" }}
              >
                <form>
                  <FormItem icon="mail" placeholder="Username" />
                  <FormItem icon="lock" placeholder="Password" />
                </form>
                {show && error && <Alert message={error}></Alert>}
                <div
                  class="login submit rounded p-2 mb-3"
                  style={{
                    background: "var(--blue-dark)",
                    width: "100%",
                  }}
                >
                  <button type= "button" class="" onClick={validateUser}>
                    ĐĂNG NHẬP
                  </button>
                </div>
                <div class="">
                  <Link to="">Quên mật khẩu</Link>
                </div>
              </div>
            </div>
          </div>
          <div class="col-3"></div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

function Alert(props) {
  return <div class="danger">{props.message}</div>;
}

export function WelcomeTagline() {
  return (
    <div className="welcome mb-5">
      <div class="fs-1 mb-3" style={{ color: "var(--red-lipstick)" }}>
        Chào mừng đến với MangaPunch!{" "}
      </div>
      <div class="fs-5">
        Đọc truyện miễn phí và vô tư trao đổi cùng các Mọt Truyện{" "}
      </div>
    </div>
  );
}

export function FormItem(props) {
  return (
    <div class="username input-group input-group-lg mb-3">
      <span class="input-group-text" id="basic-addon1">
        <span class="material-symbols-outlined">{props.icon}</span>
      </span>
      <input type="text" class="form-control" placeholder={props.placeholder} />
    </div>
  );
}
