import { useState, useEffect } from "react";
import MangaService from "../../services/manga.service";
import { Link } from "react-router-dom";
import ChapterPage from "../chapter/ChapterPage";
import { Route, useRoute } from "react-router";

export function ChapterTable(props) {
  let { path, url } = useRouteMatch();

  return (
    <div>
      <table className="chapters-table" style={{ width: "100%" }}>
        <tr>
          <th class="text-start">Tên chương</th>
          <th>Cập nhật</th>
          <th>Lượt xem</th>
        </tr>
        {props.chapters.map((chapter) => (
          <tr>
            <td class="text-start">
              <Link to={`${url}/chapters/${chapter.id}`}>{chapter.title}</Link>
            </td>
            <td>
              <UploadedDateTimeByChapterId chapterid={chapter.id} />
            </td>
            <td>{chapter.views}</td>
          </tr>
        ))}
      </table>
      <Route path={`${path}/chapters`}>
        <Route path=":chapterid" element={<ChapterPage />}></Route>
      </Route>
    </div>
  );
}

function UploadedDateTimeByChapterId(props) {
  const [datetime, setDatetime] = useState([]);

  useEffect(() => {
    MangaService.getUploadedDateTimeByChapterId(props.chapterid)
      .then((res) => {
        setDatetime(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  });

  return <>{datetime}</>;
}
