import React, { Component, useEffect, useState } from "react";
import RankingSidebar from "./RankingSidebar";
import MangaService from "../services/MangaService";

export default function HomePage() {
  return (
    <div class="container ">
      <div class="row">
        <div class="col-9">
          <MainContent />
        </div>
        <div class="col-3">
          <RankingSidebar />
        </div>
      </div>
    </div>
  );
}

function MainContent(props) {
  const [mangas, setMangas] = useState([]);
  const [mangaPageSize, setMangaSize] = useState(0);

  useEffect(() => {
    // test if axios is connecting
    MangaService.getAllMangasInPage(0)
      .then((response) => {
        setMangas(response.data.content);
        setMangaSize(response.pageable.totalPages)
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }, []);

  return (
    <section class="main-content">
      <div
        class="title d-flex align-items-center"
        style={{
          textAlign: "left",
          color: "#D61C4E",
          fontSize: "larger",
          fontWeight: "bold",
        }}
      >
        <span class="material-symbols-outlined" style={{}}>
          star
        </span>
        <span
          style={{
            marginLeft: "2px",
          }}
        >
          {" "}
          Truyện Mới Cập Nhật
        </span>
      </div>
      <div class="manga-grid me-4">
        <div class="row">
          {mangas.map((manga) => (
            <MangaItem
              mangaId={manga.id}
              thumbnailUrl={manga.thumbnailUrl}
              title={manga.title}
            />
          ))}
        </div>
      </div>
      <div class="">
        <Pagination />
      </div>
    </section>
  );
}

function Pagination(props) {
  return (
    <nav aria-label="Page navigation example">
      <ul class="pagination">
        <li class="page-item">
          <a class="page-link" href="#">
            &lt;
          </a>
        </li>
        {props.pages.map}

        <li class="page-item">
          <a class="page-link" href="#">
            1
          </a>
        </li>
        <li class="page-item">
          <a class="page-link" href="#">
            2
          </a>
        </li>
        <li class="page-item">
          <a class="page-link" href="#">
            3
          </a>
        </li>
        <li class="page-item">
          <a class="page-link" href="#">
            &gt;
          </a>
        </li>
      </ul>
    </nav>
  );
}

function MangaItem(props) {
  return (
    <div class="col-3">
      <div
        class="manga-item"
        id={props.mangaId}
        style={{ marginTop: "10px", marginBottom: "10px" }}
      >
        <a href="#">
          <img
            width={"100%"}
            height={"300px"}
            style={{ objectFit: "cover" }}
            class="rounded border"
            src={props.thumbnailUrl}
          ></img>
          <div
            class="manga-title"
            style={{
              overflow: "hidden",
              textOverflow: "ellipsis",
              fontSize: "large",
              color: "black",
              whiteSpace: "nowrap",
            }}
          >
            {props.title}
          </div>
        </a>
      </div>
    </div>
  );
}
