import React, { Component, useState } from "react";

export default class NavigationComponent extends Component {
  render() {
    const navStyle = {
      backgroundColor: "#293462",
    };

    const categoryDropdownStyle = {
      top: "0px",
      start: "0px",
    };

    return (
      <div>
        <nav style={navStyle}>
          <div className="container">
            <div class="nav-menu" style={{ fontSize: "18px" }}>
              <ul class="d-flex">
                <NavItem text={"Trang Chủ"} />
                <NavItem text={"Thể Loại"} class="position-relative">
                 
                </NavItem>
                <NavItem text={"Xếp Hạng"} />
                <NavItem text={"Tìm Truyện"} />
                <NavItem text={"Lịch Sử"} />
                <NavItem text={"Theo Dõi"} />
              </ul>
            </div>
          </div>
        </nav>
      </div>
    );
  }
}

class CategoryDropdownMenu extends Component{
    render(){
        render
    }
}

function DropdownItem(props) {
  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
    setIsHover(true);
  };
  const handleMouseLeave = () => {
    setIsHover(false);
  };

  const boxStyle = {
    backgroundColor: isHover ? "white" : "",
    padding: "6px",
  };

  const linkStyle = {
    color: isHover ? "orange" : "black",
  };

  return (
    <li
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={boxStyle}
    >
      <a style={linkStyle} class="" href="#">
        {props.text}
      </a>
    </li>
  );
}

function NavItem(props) {
  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
    setIsHover(true);
  };
  const handleMouseLeave = () => {
    setIsHover(false);
  };

  const boxStyle = {
    backgroundColor: isHover ? "white" : "",
    padding: "10px",
  };

  const linkStyle = {
    color: isHover ? "black" : "white",
  };

  return (
    <li
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={boxStyle}
    >
      <a style={linkStyle} class="" href="#">
        {props.text}
      </a>
    </li>
  );
}
