import React, { Component, useState } from "react";
import Footer from "../../common/Footer";
import Header from "../../common/Header";
import { Link } from "react-router-dom";
import { WelcomeTagline, FormItem, SubmitButton } from "../login/LoginPage";
import { useDispatch } from "react-redux";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";

export default function SignUpPage(props) {
  const [state, setState] = useState({
    fullname: "",
    email: "",
    username: "",
    password: "",
    successful: false,
  });
  const dispatch = useDispatch();

  const vpassword = (value) => {
    if (value.length < 6 || value.length > 40) {
      return (
        <div className="alert alert-danger" role="alert">
          The password must be between 6 and 40 characters.
        </div>
      );
    }
  };

  function onChangeFullname(e) {
    setState({ ...state, fullname: e.target.value });
  }

  function onChangeUsername(e) {
    setState({ ...state, username: e.target.value });
  }
  function onChangeEmail(e) {
    setState({
      ...state,
      email: e.target.value,
    });
  }
  function onChangePassword(e) {
    setState({
      ...state,
      password: e.target.value,
    });
  }
  function handleRegister(e) {
    e.preventDefault();
    setState({
      ...state,
      successful: false,
    });

    dispatch(
      register(state.fullname, state.email, state.username, state.password)
    )
      .then(() => {
        this.setState({
          successful: true,
        });
      })
      .catch(() => {
        this.setState({
          successful: false,
        });
      });
  }

  return (
    <div>
      <Header />
      <div class="container my-5">
        <div class="row">
          <div class="col-3"></div>
          <div class="col-6">
            <WelcomeTagline />
            <div className="not-a-memeber mb-2 d-flex justify-content-end align-items-center">
              <span>Tôi là thành viên? </span>
              <span class="btn btn-info ms-2">
                <Link to="/login">Đăng nhập</Link>
              </span>
            </div>
            <div className="login-box border">
              <div
                className="login-form my-5"
                style={{ marginLeft: "100px", marginRight: "100px" }}
              >
                <form onSubmit={handleRegister}>
                  <FormItem
                    icon="person"
                    placeholder="Fullname"
                    value={state.fullname}
                    onChange={(e) => onChangeFullname(e)}
                  />
                  <FormItem
                    icon="mail"
                    placeholder="Email"
                    value={state.email}
                    onChange={(e) => onChangeUsername(e)}
                  />
                  <FormItem
                    icon="person"
                    placeholder="Username"
                    value={state.username}
                    onChange={(e) => onChangeEmail(e)}
                  />
                  <FormItem
                    icon="lock"
                    placeholder="Password"
                    value={state.password}
                    onChange={(e) => onChangePassword(e)}
                  />
                  <SubmitButton name="ĐĂNG KÝ" />
                </form>
              </div>
            </div>
          </div>
          <div class="col-3"></div>
        </div>
      </div>
      <Footer />
    </div>
  );
}
