import React, { Component } from "react";
import Footer from "../../common/Footer";
import Header from "../../common/Header";
import { Link } from "react-router-dom";
import { WelcomeTagline, FormItem, SubmitButton } from "../login/LoginPage";

export default function SignUpPage(props) {
  const required = (value) => {
    if (!value) {
      return (
        <div className="alert alert-danger" role="alert">
          This field is required!
        </div>
      );
    }
  };
  const email = (value) => {
    if (!isEmail(value)) {
      return (
        <div className="alert alert-danger" role="alert">
          This is not a valid email.
        </div>
      );
    }
  };
  const vusername = (value) => {
    if (value.length < 3 || value.length > 20) {
      return (
        <div className="alert alert-danger" role="alert">
          The username must be between 3 and 20 characters.
        </div>
      );
    }
  };
  const vpassword = (value) => {
    if (value.length < 6 || value.length > 40) {
      return (
        <div className="alert alert-danger" role="alert">
          The password must be between 6 and 40 characters.
        </div>
      );
    }
  };

  return (
    <div>
      <Header />
      <div class="container my-5">
        <div class="row">
          <div class="col-3"></div>
          <div class="col-6">
            <WelcomeTagline />
            <div className="not-a-memeber mb-2 d-flex justify-content-end align-items-center">
              <span>Tôi là thành viên? </span>
              <span class="btn btn-info ms-2">
                <Link to="/login">Đăng nhập</Link>
              </span>
            </div>
            <div className="login-box border">
              <div
                className="login-form my-5"
                style={{ marginLeft: "100px", marginRight: "100px" }}
              >
                <form>
                  <FormItem icon="person" placeholder="Fullname" />
                  <FormItem icon="mail" placeholder="Email" />
                  <FormItem icon="person" placeholder="Username" />
                  <FormItem icon="lock" placeholder="Password" />
                </form>
                <SubmitButton name="ĐĂNG KÝ" />
              </div>
            </div>
          </div>
          <div class="col-3"></div>
        </div>
      </div>
      <Footer />
    </div>
  );
}
