import React from "react";
import { useState, useEffect } from "react";
import MangaService from "../../services/MangaService";

export function FavoriteButton(props) {
  const [isFavorited, setIsFavorited] = useState(true);
  const [favorites, setFavorites] = useState(0);

  const userid = props.user == null ? 0 : props.user.id;
  useEffect(() => {
    MangaService.isMangaFavoritedByUser(props.mangaid, userid)
      .then((res) => {
        setIsFavorited(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }, []);

  function handleAddToFavorite() {
    setIsFavorited(true);
    MangaService.addMangaToFavorite(props.mangaid, userid)
      .then((res) => {
        console.log(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });

    MangaService.getFavoriteNumberByMangaId(props.mangaid)
      .then((res) => {
        setFavorites(res.data);
        console.log();
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }

  function handleCancelFavorite() {
    setIsFavorited(false);
  }
  return (
    <div>
      {!isFavorited && (
        <button
          type="button"
          class="btn btn-primary d-flex align-items-center me-2"
          disabled={!props.isLoggedIn}
          onClick={handleAddToFavorite}
        >
          <span class="material-symbols-outlined me-2">favorite</span>
          <span>Theo dõi</span>
        </button>
      )}

      {/*  user favorited manga, show "cancel favorite" button */}
      {isFavorited && (
        <button
          type="button"
          class="btn btn-danger d-flex align-items-center me-2"
          onClick={handleCancelFavorite}
        >
          <span class="material-symbols-outlined me-2">close</span>
          <span>Bỏ Theo dõi</span>
        </button>
      )}
      <span>
        <span className="fw-bold">{favorites}</span> Người đã theo dõi truyện
      </span>
    </div>
  );
}
