import React, { Component, useEffect, useState } from "react";
import { useParams } from "react-router";
import MangaService from "../services/MangaService";
import Footer from "./Footer";
import HeaderComponent from "./HeaderComponent";
import NavigationComponent from "./NavigationComponent";
import { Link } from "react-router-dom";

export default class ChapterPage extends Component {
  render() {
    return (
      <div>
        <HeaderComponent />
        <NavigationComponent />
        <div className="pages-main">
          <div class="container">
            <MainContent />
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}

function MainContent() {
  const [pages, setPages] = useState([]);
  const [chapter, setChapter] = useState([]);
  const [manga, setManga] = useState([]);

  const { chapterid, mangaid } = useParams();
  useEffect(() => {
    MangaService.findMangaById(mangaid)
      .then((res) => {
        setManga(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
    MangaService.getChapterById(chapterid)
      .then((res) => {
        setChapter(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
    MangaService.getPagesByChapterId(chapterid)
      .then((res) => {
        setPages(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }, []);

  return (
    <>
      <div className="chapter-title">
        <span>
          <Link to = {`../../`} style = {{textTransform : ""}}>{manga.title}</Link> - {chapter.title}
        </span>
      </div>
      <div className="chapter-images">
        {pages.map((page) => (
          <img src={page}></img>
        ))}
      </div>
    </>
  );
}
