import React, { Component, useEffect, useState } from "react";
import RankingSidebar from "./RankingSidebar";
import MangaService from "../services/MangaService";
import HeaderComponent from "./HeaderComponent";
import NavigationComponent from "./NavigationComponent";
import Footer from "./Footer";
import { useParams } from "react-router";
import TestThumbnail from "./TestThumbnail";

export default function MangaPage() {
  return (
    <div>
      <HeaderComponent />
      <NavigationComponent />
      <main class="my-4">
        <div class="container">
          <div class="row">
            <div class="col-9">
              <MainContent />
            </div>
            <div class="col-3">
              <RankingSidebar />
            </div>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

function MainContent() {
  const [manga, setManga] = useState([]);
  const [genres, setGenres] = useState([]);
  const [totalViews, setTotalViews] = useState(0);
  let { mangaid } = useParams();

  useEffect(() => {
    MangaService.findMangaById(mangaid)
      .then((res) => {
        setManga(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });

    MangaService.getGenresByMangaId(mangaid)
      .then((res) => {
        setGenres(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });

    MangaService.getTotalViewsByMangaId(mangaid)
      .then((res) => {
        setTotalViews(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }, []);

  return (
    <div>
      <div className="manga-title" class ="mb-4" style={{ fontSize: "28px", textTransform : "Uppercase", color:"var(--red-lipstick)", fontWeight:"bolder" }}>
        {manga.title}
      </div>
      <div className="manga-info">
        <div class="row">
          <div class="col-4">
            <img src={manga.thumbnailUrl} />
          </div>
          <div class="col-8 text-start" style={{ fontSize: "larger" }}>
            <div className="author" class = "mb-3">
              <div class="row g-0">
                <div class="col-2">Tác giả</div>
                <div class="col-10">{manga.author}</div>
              </div>
            </div>
            <div className="genre" class = "mb-3">
              <div class="row g-0">
                <div class="col-2">Thể loại</div>
                <div class="col-10 ">
                  {genres.map((g) => 
                    <GenreItem genre={g} />
                  )}
                </div>
              </div>
            </div>

            <div className="views" class = "mb-3">
              <div class="row g-0">
                <div class="col-2">Lượt xem</div>
                <div class="col-10">{totalViews}</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

function GenreItem(props) {
  const [isHover, setIsHover] = useState(false);

  const style = {
    border: "1px solid var(--orange-dark)",
    

  }



  return (
    <span class="rounded-pill px-2 py-1 me-2" style={style}>
      {props.genre}
    </span>
  );
}
