import React, { Component, useState } from "react";

export default class NavigationComponent extends Component {
  render() {
    const navStyle = {
      backgroundColor: "#293462",
    };

    return (
      <div>
        <nav style={navStyle} class="position-relative">
          <div className="container">
            <div class="nav-menu" style={{ fontSize: "18px" }}>
              <ul class="d-flex">
                <NavItem text={"Trang Chủ"} />
                <NavItem text={"Thể Loại"} />
                <NavItem text={"Xếp Hạng"} />
                <NavItem text={"Tìm Truyện"} />
                <NavItem text={"Lịch Sử"} />
                <NavItem text={"Theo Dõi"} />
              </ul>
            </div>
          </div>
          <div class="category-dropdown-menu position-absolute border">
            <div class="row">
              <div class="col-4">
                <CategoryDropdownItem text="Tất cả" />
                <CategoryDropdownItem text="Action" />
                <CategoryDropdownItem text="Adventure" />
                <CategoryDropdownItem text="Comedy" />
              </div>
              <div class="col-4">
                <CategoryDropdownItem text="Fantasy" />
                <CategoryDropdownItem text="Horror" />
                <CategoryDropdownItem text="Josei" />
                <CategoryDropdownItem text="Shounen" />{" "}
              </div>
              <div class="col-6">
                <CategoryDropdownItem text="Shoujo" />
                <CategoryDropdownItem text="Historical" />
                <CategoryDropdownItem text="Slice of life" />
                <CategoryDropdownItem text="Tragedy" />{" "}
              </div>
            </div>
            <ul></ul>
          </div>
        </nav>
      </div>
    );
  }
}

function CategoryDropdownItem(props) {
  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
    setIsHover(true);
  };
  const handleMouseLeave = () => {
    setIsHover(false);
  };

  const boxStyle = {
    backgroundColor: isHover ? "white" : "",
    padding: "6px",
  };

  const linkStyle = {
    color: isHover ? "orange" : "black",
  };

  return (
    <li
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={boxStyle}
    >
      <a style={linkStyle} class="" href="#">
        {props.text}
      </a>
    </li>
  );
}

function NavItem(props) {
  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
    setIsHover(true);
  };
  const handleMouseLeave = () => {
    setIsHover(false);
  };

  const boxStyle = {
    backgroundColor: isHover ? "white" : "",
    padding: "10px",
  };

  const linkStyle = {
    color: isHover ? "black" : "white",
  };

  return (
    <li
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={boxStyle}
    >
      <a style={linkStyle} class="" href="#">
        {props.text}
      </a>
    </li>
  );
}
