import MangaService from "../services/MangaService";
import { SELECT_MANGA, UNSELECT_MANGA } from "./types";
export const selectManga = (mangaid) => (dispatch) => {
  return MangaService.findMangaById(mangaid).then((res) => {
    dispatch({
      type: SELECT_MANGA,
      payload: res.data,
    }
    
    );
  });
};

export const unselectManga = () => ({
  type: UNSELECT_MANGA,
});
