import React, { Component, useEffect, useState } from "react";
import RankingSidebar from "./RankingSidebar";
import MangaService from "../services/MangaService";
import HeaderComponent from "./HeaderComponent";
import NavigationComponent from "./NavigationComponent";
import Footer from "./Footer";
import { useParams } from "react-router";
import TestThumbnail from "./TestThumbnail";

export default function MangaPage() {
  return (
    <div>
      <HeaderComponent />
      <NavigationComponent />
      <main class="my-4">
        <div class="container">
          <div class="row">
            <div class="col-9">
              <MainContent />
            </div>
            <div class="col-3">
              <RankingSidebar />
            </div>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

function MainContent() {
  const [manga, setManga] = useState([]);

  let { mangaid } = useParams();

  console.log();

  useEffect(() => {
    MangaService.findMangaById(mangaid).then((res) => {
      setManga(res.data);
    });
  }, []);

  return (
    <div>
      <div className="manga-title">{manga.title}</div>
      <div className="manga-info">
        <div class="row">
          <div class="col-2">
            <TestThumbnail height="400px" />
          </div>
          <div class="col-10">
            <div className="author">
              <div class="row">
                <div class="col-2">Tác giả</div>
                <div class="col-10">{manga.author}</div>
              </div>
            </div>
            <div className="genre"></div>
            <div className="views"></div>
          </div>
        </div>
      </div>
    </div>
  );
}
