import React, { Component, useEffect, useState } from "react";
import Header from "../../common/Header";
import Navigation from "../../common/Navigation";
import Footer from "../../common/Footer";
import RankingSidebar from "../../common/RankingSidebar";
import MangaService from "../../../services/MangaService";
import {
  MainContentTitle,
  Pagination,
  MangaItem,
} from "../../home/HomePage"
import { connect } from "react-redux";
import FavoriteButton from "../../manga/FavoriteButton";

export class FavoritePage extends Component {
  render() {
    return (
      <div>
        <Header />
        <Navigation />
        <main class="my-4">
          <div class="container">
            <div class="row">
              <div class="col-9">
                <MainContent user = {this.props.user}/>
              </div>
              <div class="col-3">
                <RankingSidebar />
              </div>
            </div>
          </div>
        </main>
        <Footer />
      </div>
    );
  }
}

function MainContent(props) {
  const [mangas, setMangas] = useState([]);
  const username = props.user.username;
  useEffect(() => {
    MangaService.getFavoriteMangaByUsername(username)
      .then((response) => {
        setMangas(response.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }, []);

  return (
    <section class="main-content">
      <MainContentTitle title="Danh Sách Theo Dõi" />
      <div class="manga-grid me-4 mb-5">
        <div class="row">
          {mangas.map((manga) => (
            <div className = "col-">
              <MangaItem
                mangaId={manga.id}
                thumbnailUrl={manga.thumbnailUrl}
                title={manga.title}
              />
              <FavoriteButton />
            </div>
          ))}
        </div>
      </div>
      <div class="">
        <Pagination totalPages={mangas.length} />
      </div>
    </section>
  );
}

function mapStateToProps(state) {
  const { user } = state.auth;
  return {user};
}

export default connect(mapStateToProps)(FavoritePage);
