import axios from "axios";
import React, { Component } from "react";

const MANGA = "manga";
const MANGA_READER_APP_URL = "http://localhost:8070";
const MANGA_URL = `${MANGA_READER_APP_URL}/api/mangas/${MANGA}`;

class MangaService {
  getAllMangas() {
    return axios.get(`${MANGA_READER_APP_URL}/api/mangas`);
  }

  getTop5MangasWithMostViews(){
    return axios.get(`${MANGA_READER_APP_URL}/api/mangas/top/mangas_with_most_views`,  {headers:{'Access-Control-Allow-Origin': '*'}})
  }
}

export default new MangaService();
