import axios from "axios";
import authHeader from "./auth-header";
const API_URL = "http://localhost:8080/api/users/";
class UserService {
  getUserDetail(id) {
    return axios.get(API_URL + `${id}`, { headers: authHeader() });
  }
  addNewUser(data) {
    return axios.post(API_URL + "/add", data, { headers: authHeader() });
  }

  editUserDetail(id) {
    return axios.put(API_URL + `/edit/${id}`, data, { headers: authHeader() });
  }
  deleteUser(id) {
    return axios.delete(API_URL + `/delete/${id}`, { headers: authHeader() });
  }
  getFavoriteMangaByUsername(username) {
    return axios.get(`${MANGA_READER_APP_URL}/users/${username}/favorites`, {
      headers: authHeader(),
    });
  }

  isMangaFavoritedByUser(mangaid, username) {
    return axios.get(
      `${MANGA_READER_APP_URL}/mangas/is-favorited?mangaid=${mangaid}&userid=${username}`,
      { headers: authHeader() }
    );
  }

  addMangaToFavorite(mangaid, username) {
    return axios.get(
      `${MANGA_READER_APP_URL}/users/add-favorite?mangaid=${mangaid}&userid=${username}`,
      { headers: authHeader() }
    );
  }

  removeMangaToFavorite(mangaid, username) {
    return axios.get(
      `${MANGA_READER_APP_URL}/users/remove-favorite?mangaid=${mangaid}&userid=${username}`,
      { headers: authHeader() }
    );
  }
}
export default new UserService();
