import FavoriteButton from "./FavoriteButton";
import { connect } from "react-redux";
import { useEffect } from "react";
import MangaService from "../../services/MangaService";
import { useState } from "react";

export function MangaFavoriteButton(props) {
  const [favorites, setFavorites] = useState(0);

  useEffect(() => {
    MangaService.getFavoriteNumberByMangaId(props.mangaid)
      .then((res) => {
        setFavorites(res.data);
        console.log("favorite ", res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }, []);
  return (
    <div>
      {!props.isLoggedIn && (
        <div className="fst-italic fw-light mb-2">
          Hãy đăng nhập để theo dõi truyện
        </div>
      )}
      <div className="d-flex">
        <FavoriteButton />
        <div>
          <span className="fw-bold">{favorites}</span> Người đã theo dõi truyện
        </div>
      </div>
    </div>
  );
}

function mapStateToProps(state) {
  const { manga } = state.auth;

  return { isLoggedIn };
}

export default connect(mapStateToProps)(MangaFavoriteButton);
