import React, { Component, useEffect, useState } from "react";
import { useParams } from "react-router";
import MangaService from "../services/MangaService";
import Footer from "./Footer";
import HeaderComponent from "./HeaderComponent";
import NavigationComponent from "./NavigationComponent";

export default class ChapterPage extends Component {
  render() {
    return (
      <div>
        <HeaderComponent />
        <NavigationComponent />
        ChapterPage
        <Footer />
      </div>
    );
  }
}

function MainContent() {
  const [chapter, setChapter] = useState([]);
  const chapterid = useParams();
  useEffect(() => {
    MangaService.getChapterById(chapterid)
      .then((res) => {
        setChapter(res.data);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }, []);

  const pageUrlArr = chapter.pageUrls.split(" ");

  return(<div className="chapter-images">
    pageUrlArr.
  </div>)
}

function ChapterImageItem(value, index ) {
    return (<img src = {props.url}></img>)
}
