import React, { Component, useState } from "react";

export default function NavigationComponent() {
  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
    setIsHover(true);
  };
  const handleMouseLeave = () => {
    setIsHover(false);
  };

  const boxStyle = {
    backgroundColor: isHover ? "white" : "",
    color: isHover ? "black" : "white",
  };

  const homepageIconStyle = {};

  const navStyle = {
    color: "#293462!important",
    fontSize: "18px",
    backgroundColor: "#D61C4E",
  };

  return (
    <div>
      <nav style={navStyle}>
        <div className="container">
          <ul class="nav text-black">
            <li
              class="nav-item"
              style={boxStyle}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            >
              <a class="nav-link active" aria-current="page" href="#">
                Trang Chủ
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#">
                Thể Loại
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#">
                Xếp Hạng
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link ">Tìm Truyện</a>
            </li>
            <li class="nav-item">
              <a class="nav-link ">Lịch Sử</a>
            </li>
            <li class="nav-item">
              <a class="nav-link ">Theo Dõi</a>
            </li>
          </ul>
        </div>
      </nav>
    </div>
  );
}
