import React, { Component } from "react";
import icon from "../../image/onepunchman-cut.png";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { Navigate } from "react-router";
export function Header(props) {
  const headerStyle = {
    backgroundColor: "var(--orange-dark)",
  };

  console.log(props.user);

  return (
    <header style={headerStyle}>
      <div class="container">
        <div
          class="row d-flex align-items-center"
          style={{ paddingLeft: "0px" }}
        >
          <div class="col-4">
            <WebLogo />
          </div>
          <div class="col-5">
            <div class="search-box position-relative ">
              <input
                type="text"
                class="form-control"
                placeholder="Bạn muốn tìm truyện gì"
                aria-label="Username"
                aria-describedby="basic-addon1"
              ></input>

              <span
                class="material-symbols-outlined position-absolute top-0 end-0 mt-2 me-2"
                id="basic-addon1"
              >
                search
              </span>
            </div>
          </div>
          <div class="col-3 d-flex justify-content-end">
            {!props.isLoggedIn && <UserLogout />}
            {props.isLoggedIn && <UserLogin currentUser={props.user} />}
          </div>
        </div>
      </div>
    </header>
  );
}

function UserLogin(props) {
  return (
    <div className="d-flex align-items-center position-relative">
      <span className="me-3">
        Xin chào,
        <span
          className="ms-2"
          style={{ color: "var(--red-lipstick)", fontWeight: "bolder" }}
        >
          {props.currentUser.username}
        </span>
        !
      </span>

      <span
        style={{ background: "var(--red-lipstick)", scale: "1.6" }}
        class="material-symbols-outlined rounded-circle"
      >
        account_circle
      </span>
      <UserMenu />
    </div>
  );
}

function UserMenu() {
  return (
    <div
      className="position-absolute rounded p-3"
      style={{ background: "white", width: "100px" }}
    >
      <div>
        <Link to="/user/setting">Hồ sơ</Link>
      </div>
      <div>
        <Link to="/user/setting">Đăng xuất</Link>
      </div>
    </div>
  );
}

function UserLogout() {
  return (
    <div>
      <Link to="/login">Đăng Nhập</Link> <span class="ms-2 me-2">|</span>{" "}
      <Link to="/signup">Đăng Ký</Link>
    </div>
  );
}

export function WebLogo() {
  const webIconStyle = {
    color: "black",
    fontSize: "36px",
  };

  return (
    <div class="web-icon">
      <Link to="/">
        <span style={webIconStyle} class="rubik-dirt-font">
          MangaPunch
        </span>
        <span>
          <img src={icon} alt="onepunch-icon" style={{ width: "78px" }}></img>
        </span>
      </Link>
    </div>
  );
}

function mapStateToProps(state) {
  const { isLoggedIn, user } = state.auth;
  return { isLoggedIn, user };
}

export default connect(mapStateToProps)(Header);
