import React, { Component, useEffect, useState } from "react";
import RankingSidebar from "./RankingSidebar";
import MangaService from "../services/MangaService";

export default function HomePage() {
  return (
    <div class="container ">
      <div class="row">
        <div class="col-9">
          <MainContent />
        </div>
        <div class="col-3">
          <RankingSidebar />
        </div>
      </div>
    </div>
  );
}

function MainContent(props) {
  const [mangas, setMangas] = useState([]);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    // test if axios is connecting
    MangaService.getAllMangasInPage(0)
      .then((response) => {
        setMangas(response.data.content);
        setTotalPages(response.data.totalPages);
      })
      .catch(function (ex) {
        console.log("Response parsing failed. Error: ", ex);
      });
  }, [totalPages]);

  return (
    <section class="main-content">
      <MainContentTitle />
      <div class="manga-grid me-4 mb-5">
        <div class="row">
          {mangas.map((manga) => (
            <MangaItem
              mangaId={manga.id}
              thumbnailUrl={manga.thumbnailUrl}
              title={manga.title}
            />
          ))}
        </div>
      </div>
      <div class="">
        <Pagination totalPages={totalPages} />
      </div>
    </section>
  );
}

function MainContentTitle() {
  return (
    <div
      class="title d-flex align-items-center"
      style={{
        textAlign: "left",
        color: "#D61C4E",
        fontSize: "larger",
        fontWeight: "bold",
      }}
    >
      <span class="material-symbols-outlined" style={{}}>
        star
      </span>
      <span
        style={{
          marginLeft: "2px",
        }}
      >
        Truyện Mới Cập Nhật
      </span>
    </div>
  );
}

function Pagination(props) {
  const [activePage, setActivePage] = useState(1);

  var pages = [];

  console.log("total page ", props.totalPages);

  for (var i = 1; i <= props.totalPages; i++) {
    pages.push(i);
    console.log("Page ", pages);
  }

  var currentPAge = "page-item";

  return (
    <nav
      class="pagination d-flex justify-content-center"
      aria-label="Page navigation"
    >
      <ul class="pagination">
        <li class={activePage == 1 ? "page-item disabled" : "page-item"} >
          <a class="page-link disabled" href="#">
            &lt;
          </a>
        </li>

        {pages.map((page) => (
          <li class={page == activePage ? "page-item active" : "page-item"}>
            <a class="page-link" href="#">
              {page}
            </a>
          </li>
        ))}
        <li class={activePage == props.totalPages ? "page-item disabled" : "page-item"}>
          <a class="page-link" href="#">
            &gt;
          </a>
        </li>
      </ul>
    </nav>
  );
}

function MangaItem(props) {
  return (
    <div class="col-3">
      <div
        class="manga-item"
        id={props.mangaId}
        style={{ marginTop: "10px", marginBottom: "10px" }}
      >
        <a href="#">
          <img
            width={"100%"}
            height={"300px"}
            style={{ objectFit: "cover" }}
            class="rounded border"
            src={props.thumbnailUrl}
          ></img>
          <div
            class="manga-title"
            style={{
              overflow: "hidden",
              textOverflow: "ellipsis",
              fontSize: "large",
              color: "black",
              whiteSpace: "nowrap",
            }}
          >
            {props.title}
          </div>
        </a>
      </div>
    </div>
  );
}
