import React, { Component, useEffect, useState,  } from "react";
import RankingSidebar from "./RankingSidebar";
import MangaService from "../services/MangaService";

export default function HomePage(){
    const[mangas, setMangas] = useState([[]]);

    useEffect(() => {
      // test if axios is connecting
      MangaService.getAllMangas().then((response) => setMangas(response.data));
    });

    return (
      <div class="container ">
        <div class="row">
          <div class="col-9">
            <div
              class="title d-flex align-items-center"
              style={{
                textAlign: "left",
                color: "#D61C4E",
                fontSize: "larger",
                fontWeight: "bold",
              }}
            >
              <span class="material-symbols-outlined" style={{}}>
                star
              </span>
              <span
                style={{
                  marginLeft: "2px",
                }}
              >
                {" "}
                Danh Sách Truyện
              </span>
            </div>
            <div class="manga-grid me-4">
              <div class="row">
                {mangas.map((manga) => { <MangaItem thumbnailUrl = {manga.thumbnailUrl} />})}
              </div>
            </div>
            <div class=""></div>
          </div>

          <div class="col-3">
            <RankingSidebar />
          </div>
        </div>
      </div>
    );
}

function MangaItem(props) {
  return (
    <div class="col-3">
      <div
        class="manga-item "
        style={{ marginTop: "10px", marginBottom: "10px" }}
      >
        <a href="#">
          <img
            width={"100%"}
            class="rounded border"
            src={props.thumbnailUrl}
          ></img>
          <div
            class="manga-title"
            style={{
              overflow: "hidden",
              textOverflow: "ellipsis",
              fontSize: "large",
              color: "black",
              whiteSpace: "nowrap",
            }}
          >
            {props.title}
          </div>
        </a>
      </div>
    </div>
  );
}
