import React, { Component, useState } from "react";

export default class NavigationComponent extends Component {
  render() {
    const navStyle = {
      fontSize: "18px",
      backgroundColor: "#293462",
    };

    return (
      <div>
        <nav style={navStyle}>
          <div className="container">
            <div class="nav-menu">
              <ul class="d-flex">
                <NavItem text={"Trang Chủ"} />
                <NavItem text={"Thể Loại"} />
                <NavItem text={"Xếp Hạng"} />
                <NavItem text={"Tìm Truyện"} />
                <NavItem text={"Lịch Sử"} />
                <NavItem text={"Theo Dõi"} />
              </ul>
            </div>
          </div>
          <div class="category-dropdown-menu position-abos">
            <div class = 'row'>
                <div class = 'col-3'>
                    <CategoryDropdownItem text = 'fasfasf'/>
                </div>
            </div>
            <ul>
            </ul>
          </div>
        </nav>
      </div>
    );
  }
}

function CategoryDropdownItem(props) {
    const [isHover, setIsHover] = useState(false);
  
    const handleMouseEnter = () => {
      setIsHover(true);
    };
    const handleMouseLeave = () => {
      setIsHover(false);
    };
  
    const boxStyle = {
      backgroundColor: isHover ? "white" : "",
      padding: "10px",
    };
  
    const linkStyle = {
      color: isHover ? "black" : "white",
    };
  
    return (
      <li
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        style={boxStyle}
      >
        <a style={linkStyle} class="" href="#">
          {props.text}
        </a>
      </li>
    );
  }

function NavItem(props) {
  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
    setIsHover(true);
  };
  const handleMouseLeave = () => {
    setIsHover(false);
  };

  const boxStyle = {
    backgroundColor: isHover ? "white" : "",
    padding: "10px",
  };

  const linkStyle = {
    color: isHover ? "black" : "white",
  };

  return (
    <li
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={boxStyle}
    >
      <a style={linkStyle} class="" href="#">
        {props.text}
      </a>
    </li>
  );
}
