import React, { Component } from "react";

export default class HomePage extends Component {
  render() {
    return (
      <div class="container">
        <div class="row">
          <div class="col-9">
            <div
              class="title d-flex align-items-center"
              style={{
                textAlign: "left",
                color: "#D61C4E",
                fontSize: "larger",
                fontWeight: "bold",
              }}
            >
              <span class="material-symbols-outlined" style={{}}>
                star
              </span>
              <span
                style={{
                  marginLeft: "2px",
                }}
              >
                {" "}
                Danh Sách Truyện
              </span>
            </div>
            <div class="manga-grid">
              <div class="row">
                <MangaItem/>
                MangaItem
              </div>
            </div>
          </div>
          <div class="col-3"></div>
        </div>
      </div>
    );
  }
}

function MangaItem(props) {
  return (
    <div class="col-4">
      <div class="">
        <img src="http://i.truyenvua.com/ebook/190x247/ket-thuc-nhat-dinh-se-co-hau_1627179577.jpg?r=r8645456"></img>
        <div class="manga-title">ket Thuc Nhat Dinh Se Co Hau</div>
      </div>
    </div>
  );
}
